mapper.select:
SELECT skill, image
FROM tags
ORDER BY id;

SELECT image
FROM user_info
where id = #{userInfoId};

SELECT DISTINCT a.id AS a_id, a.title AS a_title, a.content AS a_content, a.posted_date AS a_posted_date, (SELECT COUNT(*) FROM likes WHERE a.id = article_id) AS likes_count, (SELECT skill FROM tags WHERE id = at.tag_id) AS article_tag FROM articles AS a LEFT OUTER JOIN article_tags AS at ON a.id = at.article_id WHERE 1=1
AND title LIKE CONCAT('%' ,#{word}, '%')
ORDER BY (SELECT count(*) FROM likes WHERE a.id = article_id) DESC;
