mapper.select:
SELECT user_info_id FROM user
WHERE email
= #{email} AND password = #{password}

mapper.update:
UPDATE user_info SET user_name=#{userName},
email=#{email},
engineer_type=#{engineerType},
description=#{description}, image=#{image}
WHERE id=#{userId};

UPDATE user SET email=#{email}, password=#{password}
WHERE
user_info_id=#{userId};

mapper.insert:
DELETE FROM follow
WHERE user_info_id=#{userInfoId} AND
follow_user_info_id=#{followUserInfoId};
